function main()
{
    const widget_buttons = document.querySelectorAll<HTMLElement>( 'div.widgets>button' );
    const back = document.getElementById('back');
    if(!(back instanceof HTMLElement))
    {
        return ;
    }
    new Controls(widget_buttons, back);
}


class Controls{

    constructor(widget_buttons : NodeListOf<HTMLElement>, back : HTMLElement){
        this._handleButtonClick = this._handleButtonClick.bind( this );
        this._handleBackButton = this._handleBackButton.bind( this );
        for ( const button of widget_buttons)
		{
			button.addEventListener( 'click', this._handleButtonClick );
		}
        back.addEventListener('click', this._handleBackButton);
    }

    doAction(action : string) {
        this._switchPage(1);
        switch ( action )
        {
            case "clock":
                this.manage_form();
                break;
            case "weather":
            case "reminder":    
            case "greeting":    
               break;  
        }
    }

    _switchPage(i : number | boolean)
    {
        const outHome = document.getElementById("home_page");
        if(! outHome)
        {
            return;
        }
        const outWidget = document.getElementById("widget_page");
        if(! outWidget)
        {
            return;
        }
        if(i)
        {
            outHome.hidden = true;;
            outWidget.hidden = false;
        } else {
            outWidget.hidden = true;
            outHome.hidden = false;
        }
    }

    _handleButtonClick(event : Event){
        const target = event.target;
        if ( !( target instanceof HTMLButtonElement ) )
		{
			return;
		}
        const action = target.dataset.action || '';
		this.doAction( action );
    }

    _handleBackButton( event : Event ){
        const target = event.target;
        if ( !( target instanceof HTMLButtonElement ) )
		{
			return;
		}
        this._switchPage(0);
    }


    manage_form()
    {
        const radios = document.querySelectorAll<HTMLInputElement>( 'input' );
        this._handleButtonClick = this._handleRadioClick.bind( this );
        for (const radio of radios)
        {
            radio.addEventListener('change', this._handleRadioClick);
        } 
    }

    _handleRadioClick(event : Event){
        const target = event.target;
        if ( !( target instanceof HTMLInputElement ) )
		{
			return;
		}/*
        let widget = {
            widgetname: "Clock",
            position: target.value
        };*/

        /*let json_widget = JSON.stringify(widget);*/
        let xml = new XMLHttpRequest();
        let URL = 'widget.json';
        xml.open("POST", URL);
        xml.responseType = 'json';
        xml.send();
        console.log(xml.response);
    }

}

main();
